parameters.functionCode:
// Extract project data
const projectData = {
  projectId: $json.project.id,
  projectName: $json.project.name,
  domain: $json.project.domain,
  shareId: $json.project.shareId,
  documentPath: $json.project.documentPath,
  viewLink: $json.links.view,
  downloadLink: $json.links.download,
  timestamp: $json.timestamp
};

return [{ json: projectData }];

